mapper.insert:
insert /* act@insertAcctStg@계좌 인증단계 생성 */
  into act_stg_mst /* T. 계좌 인증 마스터 */
  ( user_id
  , acct_stg_cd
  )
values ( #{userId}
       , #{acctStgCd}
       )

update /* act@updateAcctStg@계좌 인증단계 수정 */
       act_stg_mst /* T. 계좌 인증 마스터 */
   set acct_stg_cd = #{acctStgCd}
 where user_id = #{userId}
